import { Component, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { from, Observable } from 'rxjs';
import { BookService } from './book.service';
import { Book } from './book.model';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent implements OnInit {
  allBooks: Observable<Book[]>=this.bookService.getAllBook();;
  bookForm: any;
book: Book | undefined; //modified
   bookIdUpdate = null;
 dataSaved = false;
  message = "";

  constructor(private formBuilder: FormBuilder, private routes: Router, private bookService: BookService) { }

  ngOnInit() {

    this.bookForm = this.formBuilder.group({
      bookName: ['', [Validators.required]],
      categoryId: [, [Validators.required]],
      publisherId: [, [Validators.required]],
      authorId: [, [Validators.required]],
      description:'',
      edition:'',
      price: [,[Validators.required]],
      isActive: [false]


    });
    this.loadBook();

  }



  loadBook() {
    this.allBooks = this.bookService.getAllBook();

  }
  deleteBookById(id: string) {
    if (confirm("Are You Sure To Delete This?")) {
      this.bookService.deleteBookById(id).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Deleted Successfully.";
        this.loadBook();

        this.bookForm.reset();
      })
    }
  }
  onFormSubmit() {
     this.dataSaved = false;
    const book = this.bookForm.value;
    this.createBook(book);



  }


  createBook(book: Book) {
  this.book = this.bookForm.value;

    if (this.bookIdUpdate == null) {
      this.bookService.createBook(book).subscribe(() => {
        this.loadBook();

      })
    }
    else {
      book.bookId = this.bookIdUpdate;
      this.bookService.updateBook(this.bookIdUpdate, book).subscribe(() => {

        this.loadBook();
        this.bookIdUpdate = null;
      })


    }


  }

  updateBook(book: Book,id:any) {
   this.bookService.updateBook(book,id)
  }


  loadBookToEdit(bookId: any) {
    this.bookService.getBookById(bookId).subscribe(book => {
      this.message = "";
      this.dataSaved = false;
      this.bookIdUpdate = book.bookId;
      this.bookForm.get('bookName').setValue(book['bookName']);
      this.bookForm.get('categoryId').setValue(book['categoryId']);
      this.bookForm.get('authorId').setValue(book['authorId']);
      this.bookForm.get('publisherId').setValue(book['publisherId']);

      this.bookForm.get('description').setValue(book['description']);
      this.bookForm.get('edition').setValue(book['edition']);
      this.bookForm.get('price').setValue(book['price']);
      this.bookForm.get('isActive').setValue(book['isActive']);
    });
  }

  resetForm() {
    this.bookForm.reset();
    this.message = "";
    this.dataSaved = false;
  }



}
<div class="container" style="width:100%">
  <form [formGroup]="bookForm" (ngSubmit)="onFormSubmit()">
    <div class="card">
      <div class="card-header bg-info">
        <h2 class="m-b-0 text-white">
          <i class="fa fa-plus-circle" style="font-size:20px; margin:5px;"></i><small>Book Information</small>
        </h2>
      </div>
      <div class="card-body">
        <div style="width:70%; margin: auto;">
          <table class="form-table">
            <tr>
              <td class="form-group">
                <input formControlName="bookName" type="text" placeholder="Book Name" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="categoryId" type="number" placeholder="Category Id" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="authorId" type="number" placeholder="Author Id" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="publisherId" type="number" placeholder="Publisher Id" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="description" type="text" placeholder="Description" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="edition" type="text" placeholder="Edition" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-group">
                <input formControlName="price" type="number" placeholder="Price" class="form-control">
              </td>
            </tr>
            <tr>
              <td class="form-check">
                <input class="form-check-input" type="checkbox" formControlName="isActive" name="IsActive" />
                <label class="form-check-label" for="IsActive">
                  Is Active?
                </label>
              </td>
            </tr>
            <tr>
              <td class="form-actions">
                <button class="btn btn-submit" type="submit" [disabled]="!bookForm.valid">Submit</button>
                <button class="btn btn-reset" type="reset" (click)="resetForm()">Reset</button>
              </td>
            </tr>
          </table>
        </div>
        <div style="width:100%; margin-top: 20px;">
          <table class="table table-striped">
            <thead>
              <tr>
                <th class="tbl-header">Book Name</th>
                <th class="tbl-header">Category ID</th>
                <th class="tbl-header">Publisher ID</th>
                <th class="tbl-header">Author ID</th>
                <th class="tbl-header">Description</th>
                <th class="tbl-header">Edition</th>
                <th class="tbl-header">Price</th>
                <th class="tbl-header">isActive</th>
                <th class="tbl-header">Edit</th>
                <th class="tbl-header">Delete</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let book of allBooks | async">
                <td>{{book.bookName}}</td>
                <td>{{book.categoryId}}</td>
                <td>{{book.publisherId}}</td>
                <td>{{book.authorId}}</td>
                <td>{{book.description}}</td>
                <td>{{book.edition}}</td>
                <td>{{book.price}}</td>
                <td>{{book.isActive}}</td>
                <td>
                  <button type="button" class="btn btn-info btn-edit" (click)="loadBookToEdit(book.bookId)">Edit</button>
                </td>
                <td>
                  <button type="button" class="btn btn-danger btn-delete" (click)="deleteBookById(book.bookId)">Delete</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </form>
</div>
