import { Component, OnInit } from '@angular/core';

import { Router } from '@angular/router';
import { Category } from './category.model';
import { CategoryService } from './category.service';

import { from, Observable } from 'rxjs';
import { FormBuilder, Validators } from '@angular/forms';


@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit {
  dataSaved = false;
  categoryForm: any;

  allCategory: Observable<any> = this.categoryService.getAllCategory();
  // allCategory: Observable<Category[]>;
  // allCategory!: Observable<Category[]>;
  // allCategory:any;

  categoryIdUpdate = null;
  message = "";

  constructor(private formBuilder: FormBuilder, private routes: Router,
    private categoryService: CategoryService) { }

  ngOnInit() {
    this.categoryForm = this.formBuilder.group({
      categoryName: ['', [Validators.required]],
      isActive: [false]
    });
    this.loadAllCategory();
  }

  loadAllCategory() {
    this.allCategory = this.categoryService.getAllCategory();
  }

  onFormSubmit() {
    this.dataSaved = false;
    const category = this.categoryForm.value;
    this.createCategory(category);
    this.categoryForm.reset();
  }

  loadCategoryToEdit(categoryId: any) {
    this.categoryService.getCategoryById(categoryId).subscribe(category => {
      this.message = "";
      this.dataSaved = false;
      this.categoryIdUpdate = category.categoryId;
      this.categoryForm.get('categoryName').setValue(category['categoryName']);
      this.categoryForm.get('isActive').setValue(category['isActive']);
    });
  }

  createCategory(category: Category) {
    if (this.categoryIdUpdate == null) {
      this.categoryService.createCategory(category).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Saved Successfully.";
        this.loadAllCategory();
        this.categoryIdUpdate = null;
        this.categoryForm.reset();
      }
      )
    }
    else {
      category.categoryId = this.categoryIdUpdate;
      this.categoryService.updateCategory(this.categoryIdUpdate, category).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Update Successfully.";
        this.loadAllCategory();
        this.categoryIdUpdate = null;
        this.categoryForm.reset();

      });
    }
  }

  deleteCategory(id: string) {
    if (confirm("Are You Sure To Delete This?")) {
      this.categoryService.deleteCategoryById(id).subscribe(() => {
        this.dataSaved = true;
        this.message = "Record Deleted Successfully.";
        this.loadAllCategory();
        this.categoryIdUpdate = null;
        this.categoryForm.reset();
      })
    }
  }

  resetForm() {
    this.categoryForm.reset();
    this.message = "";
    this.dataSaved = false;
  }

}

<div class="container" style="width:100%">
  <form [formGroup]="categoryForm" (ngSubmit)="onFormSubmit()">
    <div class="card">
      <div class="card-header bg-info">
        <h2 class="m-b-0 text-white">
          <i class="fa fa-plus-circle" style="font-size:20px; margin:5px;"></i><small>Category Information</small>
        </h2>
      </div>
      <div>

        <div style="width:50%">
          <div>
            <table>
              <tr>
                <td class="tbl1">
                  <div class="demo-full-width">
                    <input formControlName="categoryName" type="text" placeholder="Category Name" class="form-control">
                  </div>
                  <div>
                    <span *ngIf="!categoryForm.get('categoryName').value && categoryForm.get('categoryName').touched"></span>
                  </div>
                </td>
              </tr>

              <tr>
                <td class="form-check">
                  <input class="form-check-input" type="checkbox" formControlName="isActive" name="IsActive" />
                  <label class="form-check-label" for="IsActive">
                    Is Active?
                  </label>
                </td>
              </tr>

              <tr>
                <td></td>
                <td class="content-center btn-toolbar">
                </td>
                <td>
                  <p *ngIf="dataSaved" style="color:rgb(0, 128, 0);font-size:20px;font-weight:bold" Class="success" align="left">
                    {{message}}
                  </p>
                </td>
              </tr>

            </table>

          </div>
          <div>
            <button class="btn btn-primary" type="submit" [disabled]="!categoryForm.valid" style="background-color:darkblue; color:white">Submit</button>&nbsp;
            <button class="btn btn-info" type="reset" (click)="resetForm()">Reset</button>
          </div>
        </div>

        <table class="table">
          <tr ngclass="btn-primary">
            <th class="tbl2">Category Name</th>
            <th class="tbl2">Is Active?</th>
            <th class="tbl2">Edit</th>
            <th class="tbl2">Delete</th>
          </tr>
          <tr *ngFor="let category of allCategory | async">
            <td class="tbl2">{{category.categoryName}}</td>
            <td class="tbl2">{{category.isActive}}</td>
            <td class="tbl2">
              <button type="button" class="btn btn-info" (click)="loadCategoryToEdit(category.categoryId)">Edit</button>
            </td>
            <td class="tbl2">
              <button type="button" class="btn btn-danger" (click)="deleteCategory(category.categoryId)">Delete</button>
            </td>
          </tr>
        </table>
      </div>
    </div>

  </form>

</div>
